->distance($request->lat, $request->lng, $request->lat2, $request->lng2);        $distance2 = 0;        if($request->form_type == 'flat')        {            $distance2 = $this->distance($request->lat3, $request->lng3, $request->lat4, $request->lng4);        }        $distance = $distance1 + $distance2;        $requestData['pickup_location']         = $request->pickup_location;        $requestData['round_pickup_location']   = $request->round_pickup_location;        $requestData['dropoff_location']        = $request->dropoff_location;        $requestData['round_dropoff_location']  = $request->round_dropoff_location;        $requestData['pickup_date']             = date('Y-m-d', strtotime(str_replace('/', '-', $request->pickup_date)));        $requestData['round_pickup_date']       = date('Y-m-d', strtotime(str_replace('/', '-', $request->round_pickup_date)));        $requestData['pickup_time']             = $pickup_time;        $requestData['round_pickup_time']       = $round_pickup_time;        $requestData['round_trip']              = $request->form_type != 'flat'?0:1;        $requestData['hourly']                  = $request->form_type == 'hourly'?1:0;        $requestData['number_of_hours']         = $request->number_of_hours??0;        $requestData['distance1']               = $distance1;        $requestData['distance2']               = $distance2;        $requestData['distance']                = $distance;        $requestData['pickup_lat']              = $request->lat;        $requestData['pickup_lng']              = $request->lng;        $requestData['dropoff_lat']             = $request->lat2;        $requestData['dropoff_lng']             = $request->lng2;        $requestData['round_pickup_lat']        = $request->lat3;        $requestData['round_pickup_lng']        = $request->lng3;        $requestData['round_dropoff_lng']       = $request->lng4;        $requestData['round_dropoff_lat']       = $request->lat4;        $request->session()->put('ride', $requestData);        return redirect('/ride-request/create-step2');    }    /**     * Show the step 2 Form for creating a new product.     *     * @return \Illuminate\Http\Response     */    public function createStep2(Request $request)    {        $ride = $request->session()->get('ride');        if(!$ride)            return redirect('/');            /*$url = "https://maps.googleapis.com/maps/api/distancematrix/json?units=imperial&origins=".$ride['pickup_lat'].",".$ride['pickup_lng']."&destinations=".$ride['dropoff_lat'].",".$ride['dropoff_lng']."&departure_time=now&key=AIzaSyDHH2WyrHbuChuvGc1zkbY3LwiODEF8zGI";        $ch = curl_init();        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);        curl_setopt($ch, CURLOPT_URL, $url);        $result = curl_exec($ch);        $address = json_decode($result);        if(@$address->status=="OK"){            $distance = @$address->rows[0]->elements[0]->distance->text;            if($distance!=""){                $distance = explode(' ', $distance);                $distance = (double) $distance[0];                $duration_in_traffic = ceil($address->rows[0]->elements[0]->duration_in_traffic->value/60);            }        }*/        $distance1  = $ride['distance1'];        $distance2  = $ride['distance2'];        $distance   = $ride['distance'];        $categories_data = Categories::get();        $categories = [];        foreach($categories_data as $category)        {//            for round trip and one way            if($ride['hourly'] == 0)            {                $fare_without_taxes1 = $category['base_fare'] * $category['cost_per_mile'] * $distance1;                $fare_without_taxes2 = $category['base_fare'] * $category['cost_per_mile'] * $distance2;                $fare_without_taxes  = $fare_without_taxes1 + $fare_without_taxes2;                $charges1 = $fare_without_taxes1 + ($fare_without_taxes1 * ($category['meet_greet_fee']/100)) + ($fare_without_taxes1 * ($category['black_car_finder_fee']/100)) + ($fare_without_taxes1 * ($category['state_wise_percentage']/100)) + ($fare_without_taxes1 * ($category['new_york_city_fee']/100));                $charges2 = 0;                if($ride['round_trip'] == 1)                {                    $charges2 = $fare_without_taxes2 + ($fare_without_taxes2 * ($category['meet_greet_fee']/100)) + ($fare_without_taxes2 * ($category['black_car_finder_fee']/100)) + ($fare_without_taxes2 * ($category['state_wise_percentage']/100)) + ($fare_without_taxes2 * ($category['new_york_city_fee']/100));                }                $total_charges = $charges1 + $charges2;            } else {//            for hourly                $fare_without_taxes = $ride['number_of_hours'] * $category['advance_booking_fee'];                $charges1 = $fare_without_taxes + ($fare_without_taxes * ($category['meet_greet_fee']/100)) + ($fare_without_taxes * ($category['black_car_finder_fee']/100)) + ($fare_without_taxes * ($category['state_wise_percentage']/100)) + ($fare_without_taxes * ($category['new_york_city_fee']/100));                $charges2 = 0;                $total_charges = $charges1 + $charges2;            }            $categories[] = [                'id' => $category['id'],                'name' => $category['name'],                'image' => checkImage('categories/thumbs/'. $category['image']),                'charges1' => number_format($charges1,2),                'charges2' => number_format($charges2,2),                'total_charges' => number_format($total_charges,2),                'fare_without_taxes' => number_format($fare_without_taxes,2)            ];        }        return view('ride-request.create-step2',compact('ride', 'categories', 'distance'));    }    /**     * Post Request to store step1 info in session     *     * @param  \Illuminate\Http\Request  $request     * @return \Illuminate\Http\Response     */    public function postCreateStep2(Request $request)    {        $this->validate($request, [            'name'   => 'required',            'phone_number'  => 'required',            'email'       => 'required|email'        ]);        $ride = $request->session()->get('ride');        /*..........*/        $cat_id = $request->cat_id;        $category = Categories::findOrFail($cat_id);        $fare_without_taxes2     = 0;        $gratuty2                = 0;        $black_car_finder_fee2   = 0;        $state_wise_percentage2  = 0;        $new_york_city_fee2      = 0;        $charges2 = 0;        if($ride['hourly'] == 0)        {            $fare_without_taxes1 = $category['base_fare'] * $category['cost_per_mile'] * $ride['distance1'];            $fare_without_taxes2 = $category['base_fare'] * $category['cost_per_mile'] * $ride['distance2'];            $fare_without_taxes  = $fare_without_taxes1 + $fare_without_taxes2;            $gratuty1                = ($fare_without_taxes1 * ($category['meet_greet_fee']/100));            $black_car_finder_fee1   = ($fare_without_taxes1 * ($category['black_car_finder_fee']/100));            $state_wise_percentage1  = ($fare_without_taxes1 * ($category['state_wise_percentage']/100));            $new_york_city_fee1      = ($fare_without_taxes1 * ($category['new_york_city_fee']/100));            $charges1 = $fare_without_taxes1 + $gratuty1 + $black_car_finder_fee1 + $state_wise_percentage1 + $new_york_city_fee1;            if($ride['round_trip'] == 1)            {                $gratuty2                = ($fare_without_taxes2 * ($category['meet_greet_fee']/100));                $black_car_finder_fee2   = ($fare_without_taxes2 * ($category['black_car_finder_fee']/100));                $state_wise_percentage2  = ($fare_without_taxes2 * ($category['state_wise_percentage']/100));                $new_york_city_fee2      = ($fare_without_taxes2 * ($category['new_york_city_fee']/100));                $charges2 = $fare_without_taxes2 + $gratuty2 + $black_car_finder_fee2 + $state_wise_percentage2 + $new_york_city_fee2;            }            $gratuty                = $gratuty1 + $gratuty2;            $black_car_finder_fee   = $black_car_finder_fee1 + $black_car_finder_fee2;            $state_wise_percentage  = $state_wise_percentage1 + $state_wise_percentage2;            $new_york_city_fee      = $new_york_city_fee1 + $new_york_city_fee2;            $total_charges = $charges1 + $charges2;        } else {//            for hourly            $fare_without_taxes1 = $ride['number_of_hours'] * $category['advance_booking_fee'];            $fare_without_taxes  = $fare_without_taxes1 + $fare_without_taxes2;            $gratuty1                = ($fare_without_taxes1 * ($category['meet_greet_fee']/100));            $black_car_finder_fee1   = ($fare_without_taxes1 * ($category['black_car_finder_fee']/100));            $state_wise_percentage1  = ($fare_without_taxes1 * ($category['state_wise_percentage']/100));            $new_york_city_fee1      = ($fare_without_taxes1 * ($category['new_york_city_fee']/100));            $gratuty                = $gratuty1 + $gratuty2;            $black_car_finder_fee   = $black_car_finder_fee1 + $black_car_finder_fee2;            $state_wise_percentage  = $state_wise_percentage1 + $state_wise_percentage2;            $new_york_city_fee      = $new_york_city_fee1 + $new_york_city_fee2;            $charges1 = $fare_without_taxes1 + $gratuty + $black_car_finder_fee + $state_wise_percentage + $new_york_city_fee;            $charges2 = 0;            $total_charges = $charges1 + $charges2;        }        /*..........*/        $requestData['no_of_passengers']        = $request->no_of_passengers;        $requestData['no_of_bags']              = $request->no_of_bags;        $requestData['pickup_inst']             = ($request->pickup_inst)??'';        $requestData['dropoff_inst']            = ($request->dropoff_inst)??'';        $requestData['name']                    = $request->name;        $requestData['phone_number']            = $request->phone_number;        $requestData['email']                   = $request->email;        $requestData['additional_info']         = ($request->additional_info)??'';        $requestData['category_id']             = $cat_id;        $requestData['charges1']                = number_format($charges1,2);        $requestData['charges2']                = number_format($charges2,2);        $requestData['charges']                 = number_format($total_charges,2);        $requestData['fare_without_taxes1']     = number_format($fare_without_taxes1,2);        $requestData['gratuty1']                = number_format($gratuty1,2);        $requestData['black_car_finder_fee1']   = number_format($black_car_finder_fee1,2);        $requestData['state_wise_percentage1']  = number_format($state_wise_percentage1,2);        $requestData['new_york_city_fee1']      = number_format($new_york_city_fee1,2);        $requestData['fare_without_taxes2']     = number_format($fare_without_taxes2,2);        $requestData['gratuty2']                = number_format($gratuty2,2);        $requestData['black_car_finder_fee2']   = number_format($black_car_finder_fee2,2);        $requestData['state_wise_percentage2']  = number_format($state_wise_percentage2,2);        $requestData['new_york_city_fee2']      = number_format($new_york_city_fee2,2);        $requestData['fare_without_taxes']      = number_format($fare_without_taxes,2);        $requestData['gratuty']                 = number_format($gratuty,2);        $requestData['black_car_finder_fee']    = number_format($black_car_finder_fee,2);        $requestData['state_wise_percentage']   = number_format($state_wise_percentage,2);        $requestData['new_york_city_fee']       = number_format($new_york_city_fee,2);        $ride = array_merge($ride, $requestData);        $request->session()->put('ride', $ride);        return redirect('/ride-request/create-step3');    }    /**     * Show the step 2 Form for creating a new product.     *     * @return \Illuminate\Http\Response     */    public function createStep3(Request $request)    {        $ride = $request->session()->get('ride');        if($ride)        {            $rideRequest = new RideRequest();            $rideRequest->fill($ride);            $rideRequest->save();            $ride_request = RideRequest::latest()->first();        } else {            return redirect('/');        }        return view('ride-request.create-step3',compact('ride', 'ride_request'));    }    /**     * Post Request to store step1 info in session     *     * @param  \Illuminate\Http\Request  $request     * @return \Illuminate\Http\Response     */    public function postCreateStep3(Request $request)    {        $ride = $request->session()->get('ride');        $input = $request->all();        unset($input['_token']);        $req = new \Illuminate\Http\Request();        $res = $req->replace($input);        $result = (new \App\Http\Controllers\api\RideController())->payRideChargesForAirportCarLimo($res);        if(isset($result->status) && @$result->status==1){//            send email to admin and user//        email to user            $this->testMail($ride, $ride['email']);//        email to admin            $this->testMail($ride, 'booking@alphaitdevelopers.com');            $request->session()->forget('ride');            $ride_id = Hashids::encode($input['ride_id']);            Session::flash('success', 'Congratulations! Your payment is successfull.');            return redirect('ride-request/booking-successfull/'.$ride_id.'');        }else{//            else case            Session::flash('error', 'Payment not successfull. Please try again.');            return redirect()->back();//            dummy success response            /*$request->session()->forget('ride');            $ride_id = Hashids::encode($input['ride_id']);            Session::flash('success', 'Congratulations! Your payment is successfull.');            return redirect('ride-request/booking-successfull/'.$ride_id.'');*/        }    }    /**     * Show the step 2 Form for creating a new product.     *     * @return \Illuminate\Http\Response     */    public function bookingSuccessfull($id)    {        $ride_id = @Hashids::decode($id)[0];        $ride = RideRequest::with('category')->whereId($ride_id)->first();        if($ride)        {            return view('ride-request.booking-successfull',compact('ride'));        } else {            Session::flash('error', 'You are not authorized to view this page.');            return redirect('/');        }    }        /**     * Show the detail of user.     *     * @return \Illuminate\View\View     */    public function show($id)    {                exit;    }    /**     * Show the form for editing the specified resource.     *     * @param  int  $id     *     * @return \Illuminate\View\View     */    public function edit($id)    {                $id = Hashids::decode($id)[0];                            $stock = Stock::find($id);                return view('stocks.edit', compact('stock'));    }    /**     * Update the specified resource in storage.     *     * @param  int  $id     * @param \Illuminate\Http\Request $request     *     * @return \Illuminate\Http\RedirectResponse|\Illuminate\Routing\Redirector     */    public function update($id, Request $request)    {        $id = Hashids::decode($id)[0];                $this->validate($request, [             'name' => 'required|max:255',                        'code' => 'required|unique:stocks,code,'.$id,                        'cost' => 'required|numeric',                        'price' => 'required|numeric'                    ]);                                   $stock = Stock::findOrFail($id);                              $requestData = $request->all();                                                $stock->update($requestData);        /*-----image manipulation-----*/        if ($request->hasFile('image'))        {            $image = $request->file('image');            $image_name = $stock->id.'_'.rand(11111,99999).'.'.$image->getClientOriginalExtension();            $destinationPath = public_path('/uploads/products/thumbs');            $img = Image::make($image->getRealPath());            /*save image thumbnamil*/            $img->resize(100, 100, function ($constraint) {                $constraint->aspectRatio();            })->save($destinationPath.'/'.$image_name);            /*save original image*/            $destinationPath = public_path('/uploads/products');            $image->move($destinationPath, $image_name);            /*unlink old image*/            @unlink(public_path("/uploads/products/$stock->image"));            @unlink(public_path("/uploads/products/thumbs/$stock->image"));            $stock->image = $image_name;            $stock->save();         }                Session::flash('success', 'Stock successfully updated!');        return redirect('stocks');    }    /**     * Remove the specified resource from storage.     *     * @param  int  $id     *     * @return \Illuminate\Http\RedirectResponse|\Illuminate\Routing\Redirector     */    public function destroy($id)    {        $id = Hashids::decode($id)[0];        $stock = Stock::find($id);        if($stock){            $stock->delete();            $response['success'] = 'Stock successfully deleted!';            $status = $this->successStatus;          }else{            $response['error'] = 'Stock not exist against this id!';            $status = $this->notFoundStatus;          }                return response()->json(['result'=>$response], $status);    }    public function distance($lat1, $lon1, $lat2, $lon2) {        /*if (($lat1 == $lat2) && ($lon1 == $lon2)) {            return 0;        }        else {            $theta = $lon1 - $lon2;            $dist = sin(deg2rad($lat1)) * sin(deg2rad($lat2)) +  cos(deg2rad($lat1)) * cos(deg2rad($lat2)) * cos(deg2rad($theta));            $dist = acos($dist);            $dist = rad2deg($dist);            $miles = $dist * 60 * 1.1515;            return round($miles);        }*/        $url = "https://maps.googleapis.com/maps/api/distancematrix/json?units=imperial&origins=".$lat1.",".$lon1."&destinations=".$lat2.",".$lon2."&departure_time=now&key=AIzaSyCw3JxzgjSushvBkUbe-aB5p4lijEkJR-4";        $ch = curl_init();        curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);        curl_setopt($ch, CURLOPT_URL, $url);        $result = curl_exec($ch);        $address = json_decode($result);//        dd($address);        if(@$address->status=="OK"){            $distance = @$address->rows[0]->elements[0]->distance->text;            if($distance!=""){                $distance = explode(' ', $distance);                $distance = (double) $distance[0];                $duration_in_traffic = ceil($address->rows[0]->elements[0]->duration_in_traffic->value/60);            }            return $distance;        }    }    public function testMail($data, $email_to)    {        $categoryName = Categories::findOrFail($data['category_id'])->name;        $message = '<html>				<head>				  <title>Ride Request</title>				  <style>						#ride {						  font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;						  border-collapse: collapse;						  width: 50%;						}						#ride td, #ride th {						  border: 1px solid #ddd;						  padding: 8px;						}						#ride tr:nth-child(even){background-color: #f2f2f2;}						#ride tr:hover {background-color: #ddd;}						#ride th {						  padding-top: 12px;						  padding-bottom: 12px;						  text-align: right;						  background-color: #4CAF50;						  color: white;						  width:200px;						}					</style>				</head>				<body>				  <p>Ride Request From .'.$data['name'].' </p>				  <table id="ride">			  					  <tr>						  <th align="center">Name</th>						  <td align="center">'.$data['name'].'</td>					  </tr>					  					  <tr>						  <th align="center">Email Address</th>						  <td align="center">'.$data['email'].'</td>					  </tr>					  <tr>						  <th align="center">Contact Number</th>						  <td align="center">'.$data['phone_number'].'</td>					  </tr>					  <tr>						  <th align="center">Passengers</th>						  <td align="center">'.$data['no_of_passengers'].'</td>					  </tr>					  <tr>						  <th align="center">Pickup Date</th>						  <td align="center">'.date('d-m-Y', strtotime($data['pickup_date'])).'</td>					  </tr>					  <tr>						  <th align="center">Pickup Time</th>						  <td align="center">'.date('h:i a', strtotime($data['pickup_time'])).'</td>					  </tr>					  <tr>						  <th align="center">Pick Up Address</th>						  <td align="center">'.$data['pickup_location'].'</td>					  </tr>					  <tr>						  <th align="center">Drop Off Address</th>						  <td align="center">'.$data['dropoff_location'].'</td>					  </tr>					  <tr>						  <th align="center">Category Name</th>						  <td align="center">'.$categoryName.'</td>					  </tr>					  					  <tr>						  <th align="center">Base Fare</th>						  <td align="center">'.$data['fare_without_taxes'].'</td>					  </tr>					  					  <tr>						  <th align="center">Gratuity</th>						  <td align="center">$ '.$data['gratuty'].'</td>					  </tr>					  					  <tr>						  <th align="center">Black Car Finder Fee</th>						  <td align="center">$ '.$data['black_car_finder_fee'].'</td>					  </tr>					  					  <tr>						  <th align="center">State Wise Percentage</th>						  <td align="center">$ '.$data['state_wise_percentage'].'</td>					  </tr>					  					  <tr>						  <th align="center">New Yourk City Fee</th>						  <td align="center">$ '.$data['new_york_city_fee'].'</td>					  </tr>					  					  <tr>						  <th align="center">Total Amount</th>						  <td align="center">$ '.$data['charges'].'</td>					  </tr>					  					  <tr>					    <td colspan="2" style="color:red">Note* Toll tax may be applicable</td>                    </tr>					 					</table>				</body>				</html>				';        $emailData = [            'email_from'    => env("MAIL_USERNAME", "booking@alphaitdevelopers.com"),            'email_to'      => $email_to,            'email_subject' => 'Ride Request',            'final_content' => $message,        ];        Email::sendEmail($emailData);    }    }